import React, { useState } from "react";

/* Compact guided assistant — can be extended to call server-side AI */
export default function ChatAssistant({ compact=false }){
  const QUESTIONS = [
    "Hello — may I have your name?",
    "Briefly describe the legal issue (1-2 sentences).",
    "Which practice area best describes this? (civil/criminal/family/corporate/other)",
    "Preferred contact method (phone/email)?",
    "Best times for consultation?"
  ];
  const [open, setOpen] = useState(!compact); // if compact => collapsed initially
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState({});
  const [value, setValue] = useState("");

  const send = () => {
    if (!value.trim()) return;
    setAnswers(prev => ({...prev, [step]: value.trim()}));
    setValue("");
    if (step < QUESTIONS.length - 1) setStep(step + 1);
    else {
      setOpen(false);
      alert("Thanks — we have your details. Our office will contact you at 9738483568.");
      console.log("Collected answers:", {...answers, [step]: value.trim()});
      setStep(0);
      setAnswers({});
    }
  };

  if (!open && compact) {
    return (
      <div style={{display:"flex", gap:8, alignItems:"center"}}>
        <button className="btn btn-outline" onClick={()=>setOpen(true)}>Ask quick questions</button>
      </div>
    );
  }

  return (
    <div style={{background:"#fff", borderRadius:10, padding:12, boxShadow:"0 8px 30px rgba(11,37,69,0.06)"}}>
      <div style={{fontWeight:700, color:"#0b2545", marginBottom:8}}>SMR quick assistant</div>
      <div style={{minHeight:72, padding:8, borderRadius:8, background:"#fbfdff", marginBottom:8}}>
        <div style={{fontSize:14, color:"#6b7280"}}>{QUESTIONS[step]}</div>
      </div>
      <div style={{display:"flex", gap:8}}>
        <input value={value} onChange={e=>setValue(e.target.value)} placeholder="Type your answer..." style={{flex:1, padding:10, borderRadius:8, border:"1px solid #e6e9f2"}} onKeyDown={e=>{ if(e.key==="Enter") send(); }} />
        <button className="btn btn-primary" onClick={send}>Send</button>
      </div>
    </div>
  );
}
