import React from "react";
import { FaGavel, FaBalanceScale, FaFileContract, FaUsers } from "react-icons/fa";

const SERVICES = [
  { icon: <FaGavel />, title: "Civil Litigation", desc: "Property disputes, contract enforcement and civil suits." },
  { icon: <FaBalanceScale />, title: "Criminal Defense", desc: "Representation in criminal matters and bail applications." },
  { icon: <FaUsers />, title: "Family Law", desc: "Divorce, maintenance, custody and mediation." },
  { icon: <FaFileContract />, title: "Corporate & Contracts", desc: "Drafting, opinions, and contract negotiations." },
];

export default function Services(){
  return (
    <section id="services" style={{marginBottom:12}}>
      <h3 style={{fontSize:18, marginBottom:12}}>Practice Areas</h3>
      <div className="services">
        {SERVICES.map(s => (
          <div key={s.title} className="service">
            <div className="icon" style={{fontSize:18, color:"#0b2545"}}>{s.icon}</div>
            <div>
              <h4>{s.title}</h4>
              <p>{s.desc}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
